import { Component, OnInit } from '@angular/core';
import Pusher from 'pusher-js';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

interface MessageData {
  username: string;
  message: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  username: string = 'username';
  messages$: Observable<MessageData[]> = new Observable<MessageData[]>(); // Initialize with an empty observable
  message: string = '';

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    Pusher.logToConsole = true;

    const pusher = new Pusher('9a5470d17ebacab25ff0', {
      cluster: 'ap2'
    });

    const channel = pusher.subscribe('chat');
    channel.bind('message', (data: MessageData) => { 
      window.location.reload();
    });
    this.messages$ = this.http.get<MessageData[]>('http://localhost:8000/messages');
  }

  submit(): void {
    const messageData = {
      username: this.username,
      message: this.message
    };

    this.http.post('http://localhost:8000/msg', messageData)
      .subscribe(
        () => {
          this.message = '';
        },
        (error) => {
          console.error('Error sending message:', error);
        }
      );
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-4">
      <div class="bg-primary p-3 rounded">
        <div class="username">
          <label for="username" class="text-white">Username</label>
          <input type="text" name="username" id="username" class="form-control" placeholder="Username" [(ngModel)]="username" />
        </div>
      </div>
    </div>
    <div class="col-md-8">
      <div class="bg-light p-3 rounded">
        <div class="messages scrollarea">
          <div class="list-group" *ngFor="let msg of messages$ | async">
            <div>
              <strong>{{ msg.username }}</strong>
            </div>
            <div>
              <p>{{ msg.message }}</p>
            </div>
          </div>
        </div>
      </div>
      <form (submit)="submit()" class="mt-3">
        <input type="text" class="form-control" placeholder="Message" [(ngModel)]="message" name="message" />
        <button type="submit" class="btn btn-primary mt-2">Send</button>
      </form>
    </div>
  </div>
</div>
