import { Component, OnInit } from '@angular/core';
import Pusher from 'pusher-js';
import { HttpClient } from '@angular/common/http';

interface MessageData {
  username: string;
  message: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  username: string = 'username';
  messages: MessageData[] = [];
  message: string = '';

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    Pusher.logToConsole = true;

    const pusher = new Pusher('9a5470d17ebacab25ff0', {
      cluster: 'ap2'
    });

    const channel = pusher.subscribe('chat');
    channel.bind('message', (data: MessageData) => {
      this.messages.push(data);
    });
  }

  submit(): void {
    const messageData = {
      username: this.username,
      message: this.message
    };

    this.http.post('http://localhost:8000/msg', messageData)
      .subscribe(
        () => {
          this.message = ''; // Clear the message input on success
        },
        (error) => {
          console.error('Error sending message:', error);
        }
      );
  }

  getMessages(): void {
    this.http.get<MessageData[]>('http://localhost:8000/messages')
      .subscribe(
        (data) => {
          this.messages = data.reverse(); 
        },
        (error) => {
          console.error('Error retrieving messages:', error);
        }
      );
  }
}
