// app.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements{
  username = '';
  messages = [ ];
  message = '';

  submit(): void {}
}
<!-- app.component.html -->
<div class="container">
  <div class="username">
    <label for="username">Username</label>
    <input type="text" name="username" id="username" placeholder="User name" [(ngModel)]="username" />
  </div>
  <div class="messages scrollarea">
    <div class="list-group" *ngFor="let msg of messages">
      <div>
        <strong>{{ msg.username }}</strong>
      </div>
      <div>
        {{ msg.message }}
      </div>
    </div>
  </div>
  <form (submit)="submit()">
    <input type="text" class="form-control" placeholder="Message" [(ngModel)]="message" />
    <button type="submit">Send</button>
  </form>
</div>
