import { Component, OnInit } from '@angular/core';
import Pusher from 'pusher-js';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs'; // Import Observable

interface MessageData {
  username: string;
  message: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  username: string = 'username';
  messages$: Observable<MessageData[]>; // Use Observable<MessageData[]>
  message: string = '';

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    Pusher.logToConsole = true;

    const pusher = new Pusher('9a5470d17ebacab25ff0', {
      cluster: 'ap2'
    });

    const channel = pusher.subscribe('chat');
    channel.bind('message', (data: MessageData) => {
      // You may choose to add new messages to the existing ones.
      // this.messages.push(data);
      // Or, if you want to replace existing messages with new ones:
      // this.messages = [...this.messages, data];
    });

    // Initialize messages$ with the HTTP request.
    this.messages$ = this.http.get<MessageData[]>('http://localhost:8000/messages');
  }

  submit(): void {
    const messageData = {
      username: this.username,
      message: this.message
    };

    this.http.post('http://localhost:8000/msg', messageData)
      .subscribe(
        () => {
          this.message = ''; // Clear the message input on success
        },
        (error) => {
          console.error('Error sending message:', error);
        }
      );
  }
}
<div class="container">
  <div class="container">
    <div class="username">
      <label for="username">Username</label>
      <input type="text" name="username" id="username" placeholder="User name" [(ngModel)]="username" />
    </div>
    <div class="messages scrollarea">
      <div class="list-group" *ngFor="let msg of messages$ | async">
        <div>
          <strong>{{ msg.username }}</strong>
        </div>
        <div>
          <p>{{ msg.message }}</p>
        </div>
      </div>
    </div>
    <form (submit)="submit()">
      <input type="text" class="form-control" placeholder="Message" [(ngModel)]="message" name="message" />
      <button type="submit">Send</button>
    </form>
  </div>

  <form (submit)="submit()">
    <input type="text" class="form-control" placeholder="Message" [(ngModel)]="message" name="message" />
    <button type="submit">Send</button>
  </form>
</div>
